package me.hapyl.spigotutils.module.player.tablist;

import com.google.common.collect.Maps;
import com.mojang.authlib.properties.Property;
import me.hapyl.spigotutils.module.util.BukkitUtils;
import org.bukkit.ChatColor;
import org.bukkit.entity.Player;

import javax.annotation.Nonnull;
import java.util.Map;

/**
 * Static textures for {@link TablistEntry}.
 * <p>
 * You can generate or browse textures <a href="https://mineskin.org/">here</a>.
 */
public final class EntryTexture {

    private static final Map<ChatColor, EntryTexture> chatColorMapped = Maps.newHashMap();
    private static final Map<Player, EntryTexture> cachedPlayerTextures = Maps.newHashMap();

    /**
     * Equivalent to {@link ChatColor#BLACK}.
     */
    public static final EntryTexture BLACK = of(
            ChatColor.BLACK,
            "",
            ""
    );

    /**
     * Equivalent to {@link ChatColor#DARK_BLUE}.
     */
    public static final EntryTexture DARK_BLUE = of(
            ChatColor.DARK_BLUE,
            "",
            ""
    );

    /**
     * Equivalent to {@link ChatColor#DARK_GREEN}.
     */
    public static final EntryTexture DARK_GREEN = of(
            ChatColor.DARK_GREEN,
            "",
            ""
    );

    /**
     * Equivalent to {@link ChatColor#DARK_AQUA}.
     */
    public static final EntryTexture DARK_AQUA = of(
            ChatColor.DARK_AQUA,
            "",
            ""
    );

    /**
     * Equivalent to {@link ChatColor#DARK_RED}.
     */
    public static final EntryTexture DARK_RED = of(
            ChatColor.DARK_RED,
            "",
            ""
    );

    /**
     * Equivalent to {@link ChatColor#DARK_PURPLE}.
     */
    public static final EntryTexture DARK_PURPLE = of(
            ChatColor.DARK_PURPLE,
            "",
            ""
    );

    /**
     * Equivalent to {@link ChatColor#GOLD}.
     */
    public static final EntryTexture GOLD = of(
            ChatColor.GOLD,
            "",
            ""
    );

    /**
     * Equivalent to {@link ChatColor#GRAY}.
     */
    public static final EntryTexture GRAY = of(
            ChatColor.GRAY,
            "",
            ""
    );

    /**
     * Equivalent to {@link ChatColor#DARK_GRAY}.
     */
    public static final EntryTexture DARK_GRAY = of(
            ChatColor.DARK_GRAY,
            "",
            ""
    );

    /**
     * Equivalent to {@link ChatColor#BLUE}.
     */
    public static final EntryTexture BLUE = of(
            ChatColor.BLUE,
            "",
            ""
    );

    /**
     * Equivalent to {@link ChatColor#GREEN}.
     */
    public static final EntryTexture GREEN = of(
            ChatColor.GREEN,
            "",
            ""
    );

    /**
     * Equivalent to {@link ChatColor#AQUA}.
     */
    public static final EntryTexture AQUA = of(
            ChatColor.AQUA,
            "",
            ""
    );

    /**
     * Equivalent to {@link ChatColor#RED}.
     */
    public static final EntryTexture RED = of(
            ChatColor.RED,
            "",
            ""
    );
    /**
     * Equivalent to {@link ChatColor#LIGHT_PURPLE}.
     */
    public static final EntryTexture LIGHT_PURPLE = of(
            ChatColor.LIGHT_PURPLE,
            "",
            ""
    );

    /**
     * Equivalent to {@link ChatColor#YELLOW}.
     */
    public static final EntryTexture YELLOW = of(
            ChatColor.YELLOW,
            "",
            ""
    );

    /**
     * Equivalent to {@link ChatColor#WHITE}.
     */
    public static final EntryTexture WHITE = of(
            ChatColor.WHITE,
            "",
            ""
    );

    private final String[] value;

    public EntryTexture(@Nonnull String value, @Nonnull String signature) {
        this.value = new String[] { value, signature };
    }

    /**
     * Gets the <code>value</code> of this texture.
     *
     * @return the value of this texture.
     */
    @Nonnull
    public String getValue() {
        return value[0];
    }

    /**
     * Gets the <code>signature</code> of this texture.
     *
     * @return the signature of this texture.
     */
    @Nonnull
    public String getSignature() {
        return value[1];
    }

    /**
     * Gets a string array containing the value and signature.
     *
     * @return a string array containing the value and signature.
     */
    @Nonnull
    public String[] asStringArray() {
        return new String[] { value[0], value[1] };
    }

    protected String[] getStringArray() {
        return value;
    }

    /**
     * Creates an {@link EntryTexture} from a <code>value</code> and <code>signature</code>.
     *
     * @param value     - Value.
     * @param signature - Signature.
     * @return an entry texture.
     */
    @Nonnull
    public static EntryTexture of(@Nonnull String value, @Nonnull String signature) {
        return new EntryTexture(value, signature);
    }

    /**
     * Creates an {@link EntryTexture} from an online {@link Player}.
     *
     * @param player - Player.
     * @return an entry texture.
     */
    @Nonnull
    public static EntryTexture of(@Nonnull Player player) {
        EntryTexture texture = cachedPlayerTextures.get(player);

        if (texture == null) {
            final Property textures = BukkitUtils.getPlayerTextures(player);
            final String signature = textures.signature();

            texture = new EntryTexture(textures.value(), signature != null ? signature : "");
            cachedPlayerTextures.put(player, texture);
        }

        return texture;
    }

    @Nonnull
    public static EntryTexture of(@Nonnull ChatColor color) {
        final EntryTexture texture = chatColorMapped.get(color);

        if (texture != null) {
            return texture;
        }

        throw new IllegalArgumentException("Cannot get texture for %s!".formatted(color));
    }

    private static EntryTexture of(ChatColor color, String value, String signature) {
        final EntryTexture texture = new EntryTexture(value, signature);

        chatColorMapped.put(color, texture);
        return texture;
    }

}
